func getIcon(s string) io.Reader {
	switch s {
	case "01n":
		s = iconMoon
	case "01d":
		s = iconSun
	case "02d":
		s = iconCloudSun
	case "02n":
		s = iconCloudMoon
	case "03d":
		s = iconCloudSun
	case "03n":
		s = iconCloudMoon
	case "04d":
		s = iconCloud
	case "04n":
		s = iconCloud
	case "09d":
		s = iconCloudSunRain
	case "09n":
		s = iconCloudMoonRain
	case "10d":
		s = iconCloudRain
	case "10n":
		s = iconCloudMoonRain
	case "11d":
		s = iconPooStorm
	case "11n":
		s = iconPooStorm
	case "13d":
		s = iconSnowFlake
	case "13n":
		s = iconSnowFlake
	case "50d":
		s = "fog"
	case "50n":
		s = "fog"
	}

	return bytes.NewReader([]byte(s))
}
